import React from 'react';
import useStyles from './styles';
import { Card, CardActions, CardContent, CardMedia, Button, Typography } from '@material-ui/core';
import ThumbUpAltIcon from '@material-ui/icons/ThumbUpAlt';
import DeleteIcon from '@material-ui/icons/Delete';
import EditIcon from '@material-ui/icons/Edit';
import moment from 'moment';
import { useDispatch } from 'react-redux';
import { deletePost, likePost } from '../../../actions/posts';
import Divider from '@material-ui/core/Divider';

const Post = ({ post, setCurrentId}) => {
    const dispatch = useDispatch();
    const classes = useStyles();

    return (
        <Card className={classes.card}>
            <CardMedia className={classes.media} image={post.selectedFile} title={post.title}/>
            <div className={classes.overlay2}>
                <Button style={{color:'white'}} size="small" onClick={() => setCurrentId(post._id)}>
                    <EditIcon fontSize="default" />
                </Button>
            </div>
            <div className={classes.details}>

                {/* <Typography variant="body2" color="textSecondary" >{post.tags.map((tag)=>`#${tag} `)}</Typography> */}
            </div>

            <CardContent className={classes.detailsContainer}>
                <Typography className={classes.time} variant="subtitle2">{moment(post.createdAt).fromNow()}</Typography>
                <Typography className={classes.title} variant="h5"  gutterBottom>{post.title}</Typography>
                <Typography  className={classes.description} variant="body2" color="textSecondary" component="p" gutterBottom>{post.message}</Typography>
                <Typography variant="body2" color="textSecondary" >{post.tags.map((tag)=> <p className={classes.tags}>`#${tag} ` </p>)}</Typography>
            </CardContent>
            <Divider variant="middle" />
            <CardActions className={classes.cardActions }>
                By <Typography variant="h6">{post.creator}</Typography> 
                <Button size="small" color="primary" onClick={()=>{dispatch(likePost(post._id))}}>
                    <ThumbUpAltIcon fontSize="small" />
                    &nbsp;{post.likeCount}
                </Button>
                <Button size="small" color="primary" onClick={()=>{dispatch(deletePost(post._id))}}>
                    <DeleteIcon fontSize="small" />
                </Button>
            </CardActions>
        </Card>
    );
};

export default Post;
