import { makeStyles } from '@material-ui/core/styles';

export default makeStyles({
  media: {
    height: '8rem',
    objectFit:'contain',
    paddingTop: '56.25%'
  },
  description:{
    marginTop:'-18px',
    padding:'10px',
    maxHeight:'100px',
    overflow:'hidden',
    textOverflow: 'ellipsis',
  },
  card: {
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'space-between',
    borderRadius: '5px',
    height: '100%',
    position: 'relative',
  },
  detailsContainer : {
    height:'400px'
  },
  overlay2: {
    position: 'absolute',
    top: '20px',
    right: '20px',
    color: 'white',
  },
  grid: {
    display: 'flex',
  },
  details: {
    display: 'flex',
    justifyContent: 'space-between',
    margin: '20px',
  },
  title: {
    padding: '0 18px',
    marginTop:'-14px'
  },
  cardActions: {
    padding: '0 16px 8px 16px',
    display: 'flex',
    justifyContent: 'space-between',
  },
});
